mapper.select:
SELECT p.p_id, p.p_name, p.p_price, p.p_title, p.p_content, p.p_date, p.p_udate, p.p_quantity, p.p_categoryCode, p.p_discount, a.uuid AS attachUuid, a.uploadPath AS attachUploadPath, a.filename AS attachFilename, c.name AS p_categoryName FROM tbl_product p LEFT JOIN tbl_attach a ON p.p_id = a.p_id LEFT JOIN tbl_category c ON p.p_categoryCode = c.id
WHERE p.p_name LIKE CONCAT('%', #{keyword}, '%')
ORDER BY p.p_id DESC LIMIT #{offset}, #{amount}
